import React,{Component} from 'react'
import './App.css';
import Item from './Item'
class App extends React.Component{
	constructor(props) {
	    super(props);
	   
	    this.activateLasers = this.activateLasers.bind(this);//手动绑定
	}
	state = {STARTING_TODOS:[
	  { id: 1, description: 'Eat Breakfast', completed: true },
	  { id: 2, description: 'Deploy to Heroku', completed: false },
	  { id: 3, description: 'Check Email', completed: true },
	  { id: 4, description: 'Eat Lunch', completed: false }
	]}
	activateLasers(event) {
	   const {STARTING_TODOS}=this.state
	   STARTING_TODOS.unshift({ id: 1, description: 'Eat Breakfast', completed: true })
	   this.setState({
	         STARTING_TODOS: 'sss',
	   })
	}
	render(){
		const {STARTING_TODOS}=this.state
		return(
		  <div className="App">
			 <h1>If you can dream it,you can TODO it!</h1>
			 <h2>There are no tasks remaining! You shuold add one</h2>

			 <a href="#" onClick={this.activateLasers(this)} >Add task</a> 
		  </div>
		)
	}
}
export default App
